import React, { ReactNode } from 'react';
import {
    Streamlit, StreamlitComponentBase, withStreamlitConnection
} from 'streamlit-component-lib';

interface State {
  prompt: string;
  tree: string[][][];
  clicked: boolean[][];
}

const customContainer = {
  width: '100%',
  maxWidth: '100%',
  minHeight: '400px',
  position: 'relative',
};

const buttonColumn = {
  position: 'relative',
  minHeight: '130px',
};

const inputContainer = {
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center',
  position: 'absolute',
  bottom: '0',
  width: '100%',
};

const textInputStyle = {
  width: '150px',
  marginTop: '1rem',
  display: 'none',
};

const notSelectedText = {
  color: 'white',
  fontStyle: 'italic',
  position: 'absolute',
  bottom: '65px',
};

class MyComponent extends StreamlitComponentBase<State> {
  public state: State = {
    prompt: 'Why is the sky blue?',
    tree: [
      [['Because God said so'], ['Because of Raleigh scattering']],
    ],
    clicked: [],
  };

  private addToTree = (level: number, index: number, text: string) => {
    const tree = [...this.state.tree] as string[][][];
    tree[level] = tree[level] || [];
    tree[level][index] = [text];
    this.setState({ tree });
  };

  private handleClick = (level: number, index: number) => {
    const clicked = [...this.state.clicked] as boolean[][];
    clicked[level] = clicked[level] || [];
    clicked[level][index] = true;
    clicked[level][1 - index] = false;
    this.setState({ clicked });
  };

  private renderNode = (level: number, index: number) => {
    const content = this.state.tree[level]?.[index]?.[0];
    const isClicked = this.state.clicked[level]?.[index];
    const otherButtonClicked = this.state.clicked[level]?.[1 - index];

    if (content) {
      return (
        <div
          className="d-flex flex-column align-items-center"
          style={buttonColumn}
        >
          <button
            onClick={() => {
              this.handleClick(level, index);
              if (isClicked) {
                this.addToTree(level + 1, index * 2, 'Response A');
              }
            }}
            className={`btn btn-${isClicked ? 'secondary' : 'primary'} my-1`}
          >
            {content}
          </button>
          <span
            style={{
              ...notSelectedText,
              display: !isClicked && otherButtonClicked ? 'block' : 'none',
            }}
          >
            This response was not selected
          </span>
          <div style={inputContainer}>
            <input
              type="range"
              min="0"
              max="100"
              defaultValue="50"
              style={{ width: '150px', marginTop: '1rem' }}
            />
            <input
              type="text"
              placeholder="Enter your commentary"
              style={{
                ...textInputStyle,
                display: isClicked ? 'block' : 'none',
              }}
            />
          </div>
        </div>
      );
    }
    return null;
  };

  public render = (): ReactNode => {
    return (
      <div className="container" style={customContainer}>
        <div
          style={{
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            marginTop: '2rem',
          }}
        >
          <button
            style={{
              backgroundColor: '#007bff',
              border: '1px solid #007bff',
              borderRadius: '4px',
              color: 'white',
              padding: '0.5rem 1rem',
              margin: '1rem',
              pointerEvents: 'none',
            }}
          >
            {this.state.prompt}
          </button>
          <div
            style={{
              display: 'flex',
              justifyContent: 'center',
              alignItems: 'flex-end',
              marginTop: '2rem',
            }}
          >
            <div
              style={{
                display: 'flex',
                marginLeft: '-12rem',
                marginRight: '-12rem',
              }}
            >
              <div className="d-flex flex-column align-items-center">
                {this.renderNode(0, 0)}
              </div>
              <div className="d-flex flex-column align-items-center">
                {this.renderNode(0, 1)}
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  };
}
export default withStreamlitConnection(MyComponent);



