import React, { useState } from 'react';
import {
  Streamlit, StreamlitComponentBase, withStreamlitConnection
} from 'streamlit-component-lib';

const customContainer = {
  width: '100%',
  maxWidth: '100%',
  minHeight: '1000px',
};

interface MyComponentState {
  tree: any[];
}

interface ChoicePairProps {
  level: number;
  index: number;
  content: string[];
  updateTree: (level: number, index: number, textInput: string) => void;
}

const ChoicePair: React.FC<ChoicePairProps> = ({ level, index, content, updateTree }) => {
  const [clicked, setClicked] = useState([false, false]);
  const [textInput, setTextInput] = useState('');

  const handleClick = (btnIndex: number) => {
    const newClickedState = clicked.map((c, i) => i === btnIndex);
    setClicked(newClickedState);
    setTextInput('');
  };

  const handleTextInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setTextInput(e.target.value);
  };

  const handleButtonClick = () => {
    updateTree(level, index, textInput);
    setTextInput('');
  };

  return (
    <div className="choice-pair">
      <div style={{ display: 'flex', justifyContent: 'center' }}>
        {content.map((btnContent, btnIndex) => (
          <div
            key={btnIndex}
            className="d-flex flex-column align-items-center"
            style={{
              marginRight: btnIndex === 0 ? '2rem' : undefined,
              marginLeft: btnIndex === 1 ? '2rem' : undefined,
            }}
          >
            <button
              onClick={() => handleClick(btnIndex)}
              className={`btn btn-${clicked[btnIndex] ? 'secondary' : 'primary'
                } my-1`}
              style={{
                marginBottom: '1.5rem',
                whiteSpace: 'normal',
                width: '12rem',
              }}
            >
              {btnContent}
            </button>
            <div
              style={{
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
                minHeight: '1.5rem',
              }}
            >
              {!clicked[btnIndex] && clicked[1 - btnIndex] && (
                <span style={{ color: 'white', fontStyle: 'italic' }}>
                  This response was not selected
                </span>
              )}
            </div>
            {clicked[btnIndex] && (
              <input
                type="range"
                min="0"
                max="100"
                defaultValue="50"
                style={{ width: '150px', marginBottom: '1rem' }}
              />
            )}
            {clicked[btnIndex] && (
              <div
                className="input-group mb-3"
                style={{ marginTop: '1rem', width: '300px' }}
              >
                <input
                  type="text"
                  className="form-control"
                  placeholder="Enter your commentary"
                  value={textInput}
                  onChange={handleTextInputChange}
                />
                <button
                  className="btn btn-primary"
                  type="button"
                  onClick={handleButtonClick}
                >
                  Critique
                </button>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

class MyComponent extends StreamlitComponentBase<MyComponentState> {
  constructor(props: Readonly<any>) {
    super(props);

    this.state = {
      tree: [
        {
          level: 0,
          index: 0,
          content: [
            'Because God said so',
            'Because of Raleigh scattering',
          ],
          children: [],
          critiques: {
            0: '',
            1: '',
          },
        },
      ],
    };
  }

  updateTree = (level: number, index: number, textInput: string) => {
    const tree = JSON.parse(JSON.stringify((this.state as MyComponentState).tree));
    const parentNode = tree.find((node: any) => node.level === level && node.index === index);

    // Check if parentNode has the 'critiques' property and create it if needed
    if (!parentNode.critiques) {
      parentNode.critiques = {
        0: '',
        1: '',
      };
    }

    const newIndex = Math.max(
      ...tree
        .filter((node: any) => node.level === level + 1)
        .map((node: any) => node.index)
    ) + 1;

    const newNode = {
      level: level + 1,
      index: newIndex,
      content: [
        `Level ${level + 1}, Index ${newIndex * 2} response: ${parentNode.critiques[index]}`,
        `Level ${level + 1}, Index ${newIndex * 2 + 1} response: ${parentNode.critiques[index]}`,
      ],
      children: [],
      critiques: {
        0: '',
        1: '',
      },
    };

    // Store the entered critique text for the selected button
    parentNode.critiques[index] = textInput;

    tree.push(newNode);
    this.setState({ tree });
  };


  render() {
    const { tree } = (this.state as MyComponentState);

    return (
      <div className="container" style={customContainer}>
        <div
          style={{
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            marginTop: '2rem',
          }}
        >
          <button
            style={{
              backgroundColor: '#007bff',
              border: '1px solid #007bff',
              borderRadius: '4px',
              color: 'white',
              padding: '0.5rem 1rem',
              margin: '1rem',
              pointerEvents: 'none',
            }}
          >
            Why is the sky blue?
          </button>
          <div>
            {tree.map((node, index) => (
              <div key={index}>
                <ChoicePair
                  level={node.level}
                  index={node.index}
                  content={node.content}
                  updateTree={this.updateTree}
                />
                {index < tree.length - 1 && <hr />}
              </div>
            ))}
          </div>
        </div>
      </div>
    );
  }
}

export default withStreamlitConnection(MyComponent);




