import React, { ReactNode } from 'react';
import {
    Streamlit, StreamlitComponentBase, withStreamlitConnection
} from 'streamlit-component-lib';

import Tree, { mutateTree, TreeData, TreeItem } from '@atlaskit/tree';

interface State {
  treeData: TreeData;
}

class MyComponent extends StreamlitComponentBase<State> {
  public state: State = {
    treeData: {
      rootId: 'prompt',
      items: {
        prompt: {
          id: 'prompt',
          children: ['response1', 'response2'],
          data: {
            title: 'Why is the sky blue?',
          },
        },
        response1: {
          id: 'response1',
          children: [],
          data: {
            title: 'Because God said so',
          },
        },
        response2: {
          id: 'response2',
          children: [],
          data: {
            title: 'Because of Raleigh scattering',
          },
        },
      },
    },
  };

  onExpand = (itemId: any) => {
    const { treeData } = this.state;
    this.setState({
      treeData: mutateTree(treeData, itemId, { isExpanded: true }),
    });
  };

  onCollapse = (itemId: any) => {
    const { treeData } = this.state;
    this.setState({
      treeData: mutateTree(treeData, itemId, { isExpanded: false }),
    });
  };

  renderItem = ({ item, provided }: RenderItemParams) => {
    return (
      <div
        ref={provided.innerRef}
        {...provided.draggableProps}
        {...provided.dragHandleProps}
        style={{
          ...provided.draggableProps.style,
          border: '1px solid white',
          borderRadius: '3px',
          backgroundColor: 'blue',
          color: 'white',
          padding: '8px',
          marginBottom: '8px',
        }}
      >
        {item.data.title}
      </div>
    );
  };

  public render = (): ReactNode => {
    const { treeData } = this.state;
    return (
      <div
        className="container"
        style={{
          width: '100%',
          maxWidth: '100%',
          minHeight: '400px',
        }}
      >
        <Tree
          tree={treeData}
          renderItem={this.renderItem}
          onExpand={this.onExpand}
          onCollapse={this.onCollapse}
        />
      </div>
    );
  };
}

export default withStreamlitConnection(MyComponent);


