import React, { ReactNode } from 'react';
import {
    Streamlit, StreamlitComponentBase, withStreamlitConnection
} from 'streamlit-component-lib';

interface State {
  prompt: string;
  tree: string[][][];
  clicked: boolean[][];
}

class MyComponent extends StreamlitComponentBase<State> {
  public state: State = {
    prompt: 'Why is the sky blue?',
    tree: [
      [['Because God said so'], ['Because of Raleigh scattering']],
    ],
    clicked: [],
  };

  private addToTree = (level: number, index: number, text: string) => {
    const tree = [...this.state.tree] as string[][][];
    tree[level] = tree[level] || [];
    tree[level][index] = [text];
    this.setState({ tree });
  };

  private handleClick = (level: number, index: number) => {
    const clicked = [...this.state.clicked] as boolean[][];
    clicked[level] = clicked[level] || [];
    clicked[level][index] = true;
    clicked[level][1 - index] = false;
    this.setState({ clicked });
  };

  private renderNode = (level: number, index: number) => {
    const content = this.state.tree[level]?.[index]?.[0];
    const isClicked = this.state.clicked[level]?.[index];

    if (content) {
      return (
        <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
          <button
            onClick={() => {
              this.handleClick(level, index);
              if (isClicked) {
                this.addToTree(level + 1, index * 2, 'Response A');
              }
            }}
            style={{
              backgroundColor: isClicked ? 'gray' : '#ffffff',
              border: '1px solid blue',
              borderRadius: '4px',
              cursor: 'pointer',
              color: 'blue',
              padding: '0.5rem 1rem',
              margin: '1rem',
              pointerEvents: 'all',
            }}
          >
            {content}
          </button>
          {!isClicked && (
            <span style={{ color: 'white', fontStyle: 'italic', marginBottom: '1rem' }}>
              This response was not selected
            </span>
          )}
          {isClicked && (
            <>
              <input
                type="text"
                placeholder="Enter a commentary"
                style={{ marginBottom: '1rem', width: '150px' }}
              />
              <div style={{ display: 'flex', marginBottom: '1rem' }}>
                {this.renderNode(level + 1, index * 2)}
                {this.renderNode(level + 1, index * 2 + 1)}
              </div>
            </>
          )}
        </div>
      );
    }
    return null;
  };

  public render = (): ReactNode => {
    return (
      <div>
        <div
          style={{
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            marginTop: '2rem',
          }}
        >
          <button
            style={{
              backgroundColor: '#007bff',
              border: '1px solid #007bff',
              borderRadius: '4px',
              color: 'white',
              padding: '0.5rem 1rem',
              margin: '1rem',
              pointerEvents: 'none',
            }}
          >
            {this.state.prompt}
          </button>
          <div
            style={{
              display: 'flex',
              justifyContent: 'center',
              alignItems: 'flex-end',
              marginTop: '2rem',
            }}
          >
            <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
              <div style={{ border: '1px solid white', width: '1px', height: '2rem' }}></div>
              <div style={{ display: 'flex', justifyContent: 'space-between', width: '24rem' }}>
                <div style={{ border: '1px solid white', height: '1px', width: '50%' }}></div>
                <div style={{ border: '1px solid white', height: '1px', width: '50%' }}></div>
              </div>
            </div>
            <div
              style={{
                display: 'flex',
                marginLeft: '-12rem',
                marginRight: '-12rem',
              }}
            >
              {this.renderNode(0, 0)}
              {this.renderNode(0, 1)}
            </div>
            <div style={{ display: 'flex', justifyContent: 'space-between', width: '24rem', marginTop: '1rem' }}>
              <input
                type="range"
                min="0"
                max="100"
                defaultValue="50"
                style={{ width: '150px' }}
              />
              <input
                type="range"
                min="0"
                max="100"
                defaultValue="50"
                style={{ width: '150px' }}
              />
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default withStreamlitConnection(MyComponent);






